    public double CalculateTax(int[][] brackets, int income) {
        if(brackets.Length == 0) return 0;
        int currentRangeUpperLimit = Math.Min(income, brackets[0][0]);
        double tax = currentRangeUpperLimit * ((double)brackets[0][1]/100);
        int total = income - currentRangeUpperLimit;
        for(int i =1;i<brackets.Length;i++)
