            currentRangeUpperLimit = Math.Min(total, brackets[i][0] - brackets[i-1][0]);
            tax = tax + (currentRangeUpperLimit * ((double)brackets[i][1]/100));
            total = total - currentRangeUpperLimit;
        }
        return tax;
    }
}
