var fillCups = function (amount) {
  const q = new MaxPriorityQueue();
  for (let i = 0; i < amount.length; i++) {
    q.enqueue(amount[i]);
  }

  let total = 0;
  while (true) {
    let a = q.dequeue().element;
    let b = q.dequeue().element;

    if (a == 0 || b == 0) {
      total = total + (a + b);
          return total;
    } else {
      total++;
      a--;
      b--;

      q.enqueue(a);
      q.enqueue(b);
    }
  }
};

