class Solution {
public:
    int fillCups(vector<int>& amount) 
    {
        priority_queue<int>q;
        for(int a:amount)
            q.push(a);
        bool flag=true;
        int sum=0;
        while(flag)
        {
            int a=q.top();
            q.pop();
            int b=q.top();
            q.pop();
            if(b==0)
