 int fillCups(vector<int>& nums) {
        priority_queue<int> max_heap;
        for(auto x : nums) {
            if(x > 0) max_heap.push(x);
        }
        int ans = 0;
        while(!max_heap.empty() && max_heap.size() >= 2){
            int val1 = max_heap.top();
            max_heap.pop();
            int val2 = max_heap.top();
            max_heap.pop();
            // 2 steps per second
            if(--val1 > 0) max_heap.push(val1);
            if(--val2 > 0) max_heap.push(val2);
            ans++; // add a second
        };
        ans += max_heap.empty() ? 0 : max_heap.top(); // only 1 step per second
        return ans;
    }
