int fillCups(vector<int>& amount) {
        priority_queue<int,vector<int>> pq;
        for(auto i:amount) if(i>0) pq.push(i);
        int time=0;
        if(pq.size()==0) return 0;
        while(pq.size()>1){
            int a=pq.top();
            pq.pop();
            int b=pq.top();
            pq.pop();
            time++;
            if(a-1>0) pq.push(a-1);
            if(b-1>0) pq.push(b-1);
        }
        if(pq.size()==1) time+=pq.top();
        return time;
