var fillCups = function(amount) {
    const sum = amount.reduce((acc, cur) => acc + cur, 0)
    const maxVal = Math.max(...amount), other = sum - maxVal


    if (maxVal >= other) {
        return maxVal
    } else {
        return (sum >> 1) + (sum & 1)
    }
};
