impl Solution {
    pub fn fill_cups(amount: Vec<i32>) -> i32 {
        let mut pq: BinaryHeap<i32> = amount.into_iter().filter(|x| *x > 0).collect();
        let mut res = 0;
        while !pq.is_empty() {
            if pq.len() >= 2 {
                let a = pq.pop().unwrap() - 1;
                let b = pq.pop().unwrap() - 1;

                if a > 0 {
                    pq.push(a);
                }
                if b > 0 {
                    pq.push(b)
                }
            } else {
                let a = pq.pop().unwrap() - 1;

                if a > 0 {
                    pq.push(a);
                }
            }

            res += 1;
        }

        res
    }
}
