class Solution {
    public int fillCups(int[] amount) {
      PriorityQueue<Integer>pq = new PriorityQueue<>((a,v)->v-a);
        for(int x : amount)
        {
            pq.offer(x);
        }

        int count = 0;
        while(!pq.isEmpty())
        {
            int first = pq.poll();
            int second = pq.poll();
            if(first == 0)return count;
            else if(second == 0)
            {
                count += first;
                return count;
            }
            else
            {
                pq.offer(first-1);
                pq.offer(second - 1);
            }
            count += 1;
        }
        return count;
    }
}
