**During Contest, I wasted a lot of time writing priority_queue and sorting solutions, but in the end, recursion saved me.**
**TC: O(N * M * O)**
**SC: O(N * M * O)**
```
class Solution {
public:
    int memo[101][101][101];
    int rec(int first, int second, int third){
        if(!first && !second && !third) return 0;

        if(memo[first][second][third] != -1) return memo[first][second][third];

        int miniAns = INT_MAX;
        if(first){
            int mini = INT_MAX;
            if(second){
                mini = min(1 + rec(first-1, second-1, third), mini);
            }

            if(third){
                mini = min(1 + rec(first-1, second, third-1), mini);
            }

            if(!second && !third){
                mini = min(1 + rec(first-1, second, third), mini);
            }
            miniAns = min(miniAns, mini);
        }

        if(second){
            int mini = INT_MAX;
            if(first){
                mini = min(1 + rec(first-1, second-1, third), mini);
            }

            if(third){
                mini = min(1 + rec(first, second-1, third-1), mini);
            }

            if(!first && !third){
                mini = min(1 + rec(first, second-1, third), mini);
            }
            miniAns = min(miniAns, mini);
        }

        if(third){
            int mini = INT_MAX;
            if(first){
                mini = min(1 + rec(first-1, second, third-1), mini);
            }

            if(second){
                mini = min(1 + rec(first, second-1, third-1), mini);
            }

            if(!first && !second){
                mini = min(1 + rec(first, second, third-1), mini);
            }
            miniAns = min(miniAns, mini);
        }

        return memo[first][second][third] = miniAns;
    }
    int fillCups(vector<int>& arr){
        if(!arr[0] && !arr[1] && !arr[2]) return 0;
        memset(memo, -1, sizeof(memo));
        return rec(arr[0], arr[1], arr[2]);
    }
};
```

