class Solution {
public:
    int fillCups(vector<int>& amount) {
        priority_queue<int> q;

            // fill qunatity of cups
    for(int i=0; i<amount.size(); i++){
        if(amount[i]>0)q.push(amount[i]);
    }

    int day = 0;
    while(!q.empty() && q.size()>1){
        // check first two elements decrease them and again push to priority queue
        int a = q.top() - 1;
        q.pop();

        int b = q.top() - 1;
        q.pop();

        day++;
        if(a>0) q.push(a);
        if(b>0) q.push(b);
    }

    // check if any element is left
    if(q.size()) day+=q.top();

    return day;

    }
};
```

