    while(!q.empty() && q.size()>1){
        // check first two elements decrease them and again push to priority queue
        int a = q.top() - 1;
        q.pop();

        int b = q.top() - 1;
        q.pop();

        day++;
        if(a>0) q.push(a);
        if(b>0) q.push(b);
    }

    // check if any element is left
    if(q.size()) day+=q.top();

    return day;

    }
};
```

