public class Solution {
    public int FillCups(int[] amount) {
        int count = 0;
        PriorityQueue<int, int> pq = new();

        foreach(var a in amount){
            if(a > 0){
                pq.Enqueue(a, -a);
            }
        }

        while(pq.Count > 1){
            count++;
            int num1 = pq.Dequeue() - 1;
            int num2 = pq.Dequeue() - 1;

            if(num1 > 0){
                pq.Enqueue(num1, -num1);
            }

            if(num2 > 0){
                pq.Enqueue(num2, -num2);
            }
        }

        return count + (pq.Count > 0 ? pq.Dequeue() : 0);
    }
}

