class Solution {
public:
    int fillCups(vector<int>& amount) {
        int s=0;
        int k=0,p=0;
      priority_queue<int>m={amount.begin(),amount.end()};
      while(m.top()!=0)
      {     s++;
           k=m.top();
           m.pop();
           k=k-1;
         p=m.top();
         m.pop();
         p=p-1;
         m.push(p);
         m.push(k);
      }
        return s;
    }
};
