class Solution {
    public int fillCups(int[] amount) {
        int sec =0;
        Arrays.sort(amount);
        int l=0;
        int r=amount.length-1;
        while(l<r){
            if(amount[l] == 0){
                l++;
            }if(amount[r] == 0){
                r--;
            }
            if(l==r) break;
            amount[l] -= 1;
            amount[r] -= 1;
            sec++;
        }
        sec += amount[l];
        return sec;
    }
}
