class Solution {
public:
    int secondsToRemoveOccurrences(string str) {

        int n = str.size();

        int time = 0;

        while(true)
        {
            // flag will keep track of is there any "01" pair in str or not

            bool flag = false;

            for(int i = 0; i < n - 1;)
            {
                // if there is "01" pair

                if(str[i] == '0' && str[i + 1] == '1')
                {
                    str[i] = '1';

                    str[i + 1] = '0';

                    flag = true;

                    i += 2;
                }

                else
                    i++;
            }

            // flag == false, means there is not any "01" pair in str, return time

            if(flag == false)
                return time;
            else
                time++;
        }

        return time;
    }
};
