impl Solution {
    pub fn seconds_to_remove_occurrences(s: String) -> i32 {
        let mut s = s.chars().collect::<Vec<char>>();
        let mut finished = false;
        let mut ret = 0;

        while finished == false {
            finished = true;
            let mut i = 0;

            while i < s.len() - 1 {
                if s[i] == '0' && s[i + 1] == '1' {
                    s.swap(i, i + 1);
                    i += 2;
                    finished = false;
                } else { 
                    i += 1; 
                }
            }

            if finished == false { ret += 1; }
        }

        ret
    }
}
