class Solution {
    public int secondsToRemoveOccurrences(String s) {
        Map<Character,Integer> hm=new HashMap<>();
        for(char c:s.toCharArray()) hm.put(c,hm.getOrDefault(c,0)+1);
        StringBuilder res=new StringBuilder();
        int op=0;
        for(int i=0;hm.containsKey('1') && i<hm.get('1');i++) res.append('1');
        for(int i=0;hm.containsKey('0') && i<hm.get('0');i++) res.append('0');
        return helper(s.toCharArray(),res.toString(),0);
    }
    int helper(char[] arr,String s,int op){
        if(s.equals(new String(arr))){
            return op;
        }
        op++;
        for(int i=0;i+1<arr.length;i++){
            if(arr[i]=='0' && arr[i+1]=='1'){
                arr=swap(i,i+1,arr);
                i++;
            }
        }
        return helper(arr,s,op);
    }
    char[] swap(int i,int j,char[] arr){
        char t=arr[i];
        arr[i]=arr[j];
        arr[j]=t;
        return arr;
    }
}
