class Solution {
public:
    int secondsToRemoveOccurrences(std::string & s) {
        int num_seconds = 0;
        bool seq_found = true;
        bool replacement_occured = false;

        while(seq_found){
                replacement_occured = false;
                // loop through and replace all occurences
                for(int i = 0; i < s.length() - 1; i++){
                    if(s.at(i) == '0' && s.at(i+1) == '1'){
                        s[i] = '1';
                        s[i+1] = '0';
                        replacement_occured = true;
                        i++; // advance ptr to prevent overlap
                    }
                }
                if(replacement_occured){
                    num_seconds++;
                }else{
                    seq_found = false;
                }            
        }


        return num_seconds;
    }
};
```

