function secondsToRemoveOccurrences(s: string): number {
  let cnt = -1;

  function count(s: string): number {
    cnt++;
    let str = s;
    let isValid = true;
    for (let i = 0; i < s.length; i++) {
      if (i < s.length - 1 && s[i] < s[i + 1]) {
        isValid = false;
        str = str.slice(0, i) + "10" + str.slice(i + 2);
      }
    }
    return isValid ? cnt : count(str);
  }

  return count(s);
};
```

