class Solution {
    public int secondsToRemoveOccurrences(String s) {
        char[] ch =s.toCharArray();
        int count=0;
        for(int i=0; i<s.length();i++){
            boolean check=true;
            int j=0;
            while(j<s.length()-1){
                if(ch[j]=='0' && ch[j+1]=='1'){
                    check=false;
                    swapped(ch,j,j+1);
                    j=j+2;
                }else{
                    j++;
                }
            }
            if(check==false)count++;
            if(check==true) return count;

        }
        return count;
    }
    private void swapped(char[] ch,int i, int j){
        char temp=ch[i];
        ch[i]=ch[j];
        ch[j]=temp;
    }
}
