            int i=0;
            string s1 = s;  //take a copy of string 
            while(i<s.size()-1)
            {

                if(s[i]=='0' and s[i+1]=='1')
                {
                    swap(s1[i],s1[i+1]);
                    c=1;
                }
                i++;
            }
            s = s1;
            res += c;
            //cout<<s<<endl;
        }
        return res;
    }
};
