public class Solution {
    public int SecondsToRemoveOccurrences(string s) {
        if (string.IsNullOrWhiteSpace(s)) {
            return 0;
        }

        int result = 0;
        int zeros = 0;
        foreach (char c in s) {
            zeros += c == '0' ? 1 : 0;
            if (c == '1' && zeros > 0) {
                result = Math.Max(result+1, zeros);
            }
        }

        return result;
    }
}
```

