    class Solution {
    public String shiftingLetters(String s, int[][] shifts) {
        int n=s.length();
        int arr[]=new int[n];
        int diff[]=new int[n+1];
        //doing it for update
        for(int i=0;i<n;i++){
            arr[i]=s.charAt(i)-'a';
        }
        //creating the diff array for l-r range update
        diff[0]=arr[0];
        for(int i=1;i<n;i++){
            diff[i]=arr[i]-arr[i-1];
        }
        for(int p[]:shifts){
            int u=p[0];
            int v=p[1];
            int w=p[2];
            //doing the range update
            if(w==0){
                diff[u]+=(-1);
                diff[v+1]-=(-1);
            }
            else{
                diff[u]+=1;
                diff[v+1]-=1;
            }
        }
        //updating the array 
        for(int i=0;i<n;i++){
            if(i==0)
                arr[i]=diff[i];
            else
                arr[i]=diff[i]+arr[i-1];  
        }
        String str="";
        for(int i=0;i<n;i++){
            //handling the negative value of arr[i]
            if(arr[i]<0){
                int c=1;
                while(Math.abs(arr[i])>(26*c)){
                    c++;
                }
                int temp=26*c;
                arr[i]+=temp;
            }
            //converting into string
            str+=(char)((arr[i]%26)+97);
        }
        return str;
    }
}

