class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        // support variables
        int len = s.size(), diffArray[len + 1], dir;
        // populating diffArray
        memset(diffArray, 0, len * sizeof(int));
        for (auto &shift: shifts) {
            dir = shift[2] ? 1 : -1;
            diffArray[shift[0]] += dir;
            diffArray[shift[1] + 1] -= dir;
        }
        // populating res
        for (int i = 0, c = 0, pos; i < len; i++) {
            c += diffArray[i];
            pos = s[i] - 'a' + c % 26;
            while (pos < 0) pos += 26;
            while (pos > 25) pos -= 26;
            s[i] = pos += 'a';
        }
        return s;
    }
};
