class Solution {
public:
    vector<int> seg;

    void upd(int l, int r, int v, int x, int lx, int rx) {
        if(lx > r or rx < l) return;
        if(lx >= l and rx <= r) {
            seg[x] += v;
            return;
        }
        int mid = (lx + rx) / 2;
        upd(l, r, v, 2 * x + 1, lx, mid);
        upd(l, r, v, 2 * x + 2, mid + 1, rx);
    }

    int query(int i, int x, int lx, int rx) {
        if(lx == rx) return seg[x];

        int mid = (lx + rx) / 2;

        if(i <= mid)
            return seg[x] + query(i, 2 * x + 1, lx, mid);

        return seg[x] + query(i, 2 * x + 2, mid + 1, rx);
    }

    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        long x = 1;
        while(x <= s.length()) x <<= 1;
        seg.resize(2 * x, 0);

        for(int i = 0; i < shifts.size(); ++i) {
            int l = shifts[i][0], r = shifts[i][1], dir;
            if(shifts[i][2] == 0) dir = -1;
            else dir = 1;

            //add 1 to the segment if direction is 1 else add -1 to the segment
            upd(l, r, dir, 0, 0, x - 1); //O(log(n))
        }

        for(int i = 0; i < s.length(); ++i) {
            // get the total changes made to the particular character
            int shift = query(i, 0, 0, x - 1);
            int dir = (shift > 0) ? 1 : -1; // direction of the shift
            shift = abs(shift) % 26; 
            // after doing 26 shifts we come back at the same position 
            //so instead of moving the character linearly get the exact location by modulo

            shift *= dir;
            int cur = s[i] - 'a'; // current position
            cur = (cur + shift + 26) % 26; // new position
            s[i] = char(cur + 'a');
        }
        return s;
    }
};
