class Solution {
public:

    void  update(vector < long long > &segtree, vector < long long > &lazy, long long ind, long long low, long long high, long long l , long long r, long long val){

        if(lazy[ind] != 0){

            segtree[ind] += (high - low + 1) * lazy[ind];
            if(low != high){
                lazy[2 * ind + 1] += lazy[ind];
                lazy[2 * ind + 2] += lazy[ind];
            }
            lazy[ind] = 0;
        }

        if(r < low || l > high || low > high) return;

        if(low >= l && high <= r){
            segtree[ind] += (high - low + 1) * val;
            if(low != high){
                lazy[2 * ind + 1] += val;
                lazy[2 * ind + 2] += val;
            }

            return;
        }

        int mid = low + (high  - low) / 2;

        update(segtree, lazy , 2 * ind + 1, low, mid, l , r, val);
        update(segtree, lazy , 2 * ind + 2, mid + 1, high, l , r, val);
        segtree[ind] = segtree[2 * ind + 1] + segtree[2 * ind + 2];

    }

    long long get(vector < long long > &segtree, vector < long long > &lazy, long long ind, long long low, long long high, long long l , long long r, long long val){

        if(lazy[ind] != 0){

            segtree[ind] += (high - low + 1) * lazy[ind];
            if(low != high){
                lazy[2 * ind + 1] += lazy[ind];
                lazy[2 * ind + 2] += lazy[ind];
            }
            lazy[ind] = 0;
        }

        if(r < low || l > high || low > high) return 0;

        if(low >= l && high <= r){

            return segtree[ind];

        }

        int mid = low + (high  - low) / 2;

        return get(segtree, lazy , 2 * ind + 1, low, mid, l , r, val) + 
        get(segtree, lazy , 2 * ind + 2, mid + 1, high, l , r, val);


    }

    string shiftingLetters(string s, vector<vector<int>>& shifts) {

        int n = s.size();

        vector < long long > segtree(4 * n,0);
        vector < long long > lazy(4 * n,0);

        for(int i = 0; i < shifts.size(); i++){
            long long val = shifts[i][2] == 1 ? 1 : -1;

            update(segtree,lazy,0,0, n - 1, shifts[i][0],shifts[i][1],val);

        }

        for(int i = 0; i < n; i++){

            long long x = get(segtree,lazy,0,0, n - 1,i,i,0);

            long long y = s[i] - 'a';

            long long z = y + x;

            if(z < 0 ) {
              z = -z;
              z = (z % 26);
              if(z != 0)
                z = -z + 26; 

            }
            else z = z % 26;

            s[i] = (char)(z + 97);


        }

        return s;
    }
};
