class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.length();
        vector<int>v1(n+1);
        for(auto it:shifts){
            int from=it[0],to=it[1],move=it[2];
            if(move==0){
                v1[from]-=1;v1[to+1]+=1;
            }else{
                v1[from]+=1;v1[to+1]-=1;
            }
        }
        for(int i=1;i<=n;i++){
            v1[i]+=v1[i-1];
        }
        string ans;
        for(int i=0;i<n;i++){
            int a=s[i]-'a';
            if(a+v1[i]>=0){
                ans.push_back('a'+((a+v1[i])%26));
            }else{
                if((a+v1[i])%26==0){
                    ans.push_back('a'+((a+v1[i])%26));
                }
                else{ans.push_back('a'+((a+v1[i])%26)+26);}
            }
        }
        return ans;
    }
};
