string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int len=s.length();
        vector<int> dir(len+1, 0);
        for(auto &shift: shifts){
            int start=shift[0], end=shift[1], job=(shift[2]==1)?1:-1;
            if(job==1)
                dir[start]++, dir[end+1]--;
            else
                dir[start]--, dir[end+1]++;
        }
        long long job=0;
