class Solution {
public:
    map<char,int> mpp ;
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        for(char ch = 'a' ; ch <= 'z' ; ++ch ) mpp[ch - 'a'] = ch ;
        vector<int> sweepForward(s.size() + 1) , sweepBackward(s.size() + 1) ;

        for(auto &x : shifts){
            int i = x[0] , j = x[1] , dir = x[2] ;
            if(dir){
                //move Forward
                sweepForward[i] += 1 ;
                sweepForward[j + 1] -= 1 ;
            }else{
                //move Backwards
                sweepBackward[i] += 1 ;
                sweepBackward[j + 1] -= 1 ;
            }
        }

        //find prefix sum of both the arrays ;
        for(int i = 1 ; i <= s.size() ; ++i) sweepBackward[i] += sweepBackward[i - 1] ,sweepForward[i] += sweepForward[i - 1] ;


        for(int i = 0 ; i < s.size() ; ++i ){
            int travel = sweepForward[i] - sweepBackward[i] ;
            travel %= 26 ;
            if(travel > 0){
                int temp = (s[i] - 'a') + travel ;
                s[i] = mpp[temp % 26] ;
            }else{
                travel *= -1 ;
                int temp = abs(26 + (s[i] - 'a') - travel) ;
                s[i] = mpp[temp % 26] ;
            }

        }

        return s ;
    }
};
