class Solution {
    char getRotateChar(int f, int b, char c) {
        if (f==b) return c;
        if (f<b) {
            b=b-f;
            b%=26;
            for(int i=0;i<b;i++) {
                c--;
                if (c=='a'-1) c='z';
            }
            return c;
        }
        else {
            f=f-b;
            f%=26;
            for(int i=0;i<f;i++) {
                c++;
                if (c=='z'+1) c='a';
            }
            return c;
        }
    }

public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.size();
        vector<int> F(n+1, 0);
        vector<int> B(n+1, 0);
        for(int i=0;i<shifts.size();i++) {
            if (shifts[i][2]==1) {
                F[shifts[i][0]]++;
                F[shifts[i][1]+1]--;
            }
            else {
                B[shifts[i][0]]++;
                B[shifts[i][1]+1]--;
            }
        }
        int f=0,b=0;
        for(int i=0;i<n;i++) {
            f+=F[i];
            b+=B[i];
            s[i]=getRotateChar(f,b,s[i]);
        }
        return s;
    }
};
```

