```js
/**
 * @param {string} s
 * @param {number[][]} shifts
 * @return {string}
 */
var shiftingLetters = function(s, shifts) {
    const cb = Array(s.length).fill(0);
    shifts.forEach(([s,e,d]) => {
        for(let i=s;i<=e;i++) {
            cb[i] += (d===0 ? -1 : 1)
        }
    })
    const arr = s.split('');
    cb.forEach((e,i) => {
        let newCharCode = arr[i].charCodeAt() + e;
        while (newCharCode < 97) {
            newCharCode+=26
        }
        while (newCharCode > 122) {
            newCharCode-=26
        }
        arr[i] = String.fromCharCode(newCharCode)
    })
    return arr.join('');
};
