class Solution {
public:
    const int mod = 26;
    string shiftingLetters(string s, vector<vector<int>>& arr) 
    {
         //getting the max end value from the arr 
         int maxval = INT_MIN;
         for(int i=0;i<arr.size();i++)
         {
             maxval = max(maxval,arr[i][1]);
         }

         //making the prefix array 
         vector<long long int>prefix(maxval+7,0);

         //processing the queries
         for(int i=0;i<arr.size();i++)
         {
             int start = arr[i][0];
             int end = arr[i][1];
             int val = arr[i][2];
             bool flag = false;

             if(val == 1) flag = true;  //true means forward shifting
             else if(val == 0) flag = false;  //false means backward shifting

             if(flag == true)    //this means for this query we need forward shifting
             {
                 prefix[start]   += 1;
                 prefix[end+1]   -= 1;
             } 
             else if(flag == false)
             {
                 prefix[start]   -= 1;
                 prefix[end+1]   +=1;
             }
         }
         for(int i=0;i<prefix.size();i++)
         {
             if(i == 0) continue;

             prefix[i] = prefix[i] + prefix[i-1];
         }
        //  for(int i=0;i<prefix.size();i++)
        //  {
        //      cout<<prefix[i]<<" ";
        //  }
         for(int i=0;i<s.length();i++)
         {
             // cout<<"i = "<<i<<endl;
              char prv_ch = s[i];

              if(prefix[i] >= 0)
              {
                   long long int val = (prv_ch - 'a');

                   //forward shifting
                   val = ((val%mod) + (prefix[i]%mod))%mod;

                   char new_ch = 'a' + val;

                   s[i] = new_ch;

              }
              else if(prefix[i] < 0)
              {
                   long long int val = (prv_ch - 'a');

                   //backward shifting
                   val = ((val%mod) - (abs(prefix[i])%mod) + mod)%mod;

                   char new_ch = 'a' + val;

                   s[i] = new_ch;
              }
         }
         return s;
    }
};
```

