                lazy[2*ind+2] += lazy[ind];
            }

            // We have done all pending updates.
            lazy[ind] = 0;
        }

        // Case 1 : If current segment is out of range.
        if(right < l || r < left) return ;

        // Case 2 : If current segment completely lies in range.
        if(l <= left && right <= r){
            segmentTree[ind] += (right - left + 1) * (value);

            if(left != right){
                lazy[2*ind+1] += value;
                lazy[2*ind+2] += value;
            }
            return ;
        }

        // Case 3 : If current segment lies partially.
        int mid = left + (right-left)/2;

        rangeUpdate(2 * ind + 1, left, mid, l, r, value);
        rangeUpdate(2 * ind + 2, mid + 1, right, l, r, value);

        segmentTree[ind] = segmentTree[2 * ind + 1] + segmentTree[2 * ind + 2];
    }

