class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {        
        Segment sT(s.size());

        // Doing Range Update in Segement trees 
        for(auto &shift : shifts){
            sT.rangeUpdateQuery(shift[0], shift[1], shift[2] ? 1 : -1);
        }


        for(int i = 0; i < s.size(); i++){
            // Fetching element updates which need to be done on current character...
            int element = (sT.querySumLazy(0, 0, s.size()-1, i,i) + s[i] - 'a');    

            // If after adding update, if our character goes negative-outside-region of our lowercase range [a....z]
            // Then we need to subtract the nodes difference from the end...
            if(((element%26) + 'a') < 97) s[i] = (char)(123 + (element%26));

            // Otherwise, if current element is in lowercase range, we will add it into answer...
            else s[i] = (char)((element%26) + 'a');
        }

        return s;
    }
};
