class Solution {
  public:
    string shiftingLetters(string s, vector < vector < int >> & shifts) {
      sort(shifts.begin(), shifts.end());
      int n = s.length();
      // to store the cummulative sum 
      vector < int > line(n + 1, 0);
      for (int i = 0; i < shifts.size(); i++) {
        auto shift = shifts[i];
        // setting the starting point
        if (shift[2]) {
          line[shift[0]] += 1;
        }
         else {
          line[shift[0]] -= 1;
        }
        // setting the ending point
        if (shift[1] < n - 1) {
          if (shift[2]) {
            line[shift[1] + 1] -= 1;
          } else {
            line[shift[1] + 1] += 1;
          }
        }
      }
      for (int i = 1; i < n; i++) {
        line[i] += line[i - 1];
      }
      for (int i = 0; i < n; i++) {
        if (line[i] == 0) continue;
        line[i] %= 26;
        s[i] = (s[i] - 'a' + line[i] + 26) % 26 + 'a';
      }
      return s;
    }
};
