class Solution:
    def shiftingLetters(self, s: str, shifts: List[List[int]]) -> str:

        def helper(char,val):
            return chr(ord('a')+(ord(char)-ord('a')+val%26)%26)

        n=len(s)
        arr=[0 for _ in range(n)]
        for start,e,d in shifts:
            arr[start]+=1 if d else -1
            if e+1<n: arr[e+1]-=1 if d else -1
        for i in range(1,n):
            arr[i]+=arr[i-1]
        for i in range(n):
            arr[i]=helper(s[i],arr[i])
        return ''.join(arr)
