class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n = s.size();
        // Scanline Algorithm
        vector<int> prefix(n);
        for(int i=0;i<shifts.size();i++)
        {
            (shifts[i][2]==1)?prefix[shifts[i][0]]+=1 : prefix[shifts[i][0]]-=1;
            if(shifts[i][1] + 1<n) (shifts[i][2]==1)?prefix[shifts[i][1] + 1]-=1 : prefix[shifts[i][1]+1]+=1;
        }

        for(int i=1;i<n;i++)
        {
            prefix[i]+=prefix[i-1];    // prefix sum  // to calculate how many times a particular index is changed
        }

        for(int i=0;i<s.size();i++)
        {
            prefix[i]%=26;    // to keep offset within 26 as a-z are 26

            if(int(s[i])+prefix[i] < 97) 
            {
                s[i] = char(int(s[i])+prefix[i]+26); // ascii value of a-z are from 97 to 122;
            }
            else if(int(s[i])+prefix[i]>122)
            {
                s[i] = char(int(s[i])+prefix[i]-26);
            }
            else
            {
                s[i] = char(int(s[i])+prefix[i]);
            }
        }
        return s;
    }
};

