class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.size();
        vector<int>offset(n+1,0);
        for(auto it:shifts){
            int l=it[0];
            int r=it[1];
            int dir=it[2];
            if(dir==0){                // go backward by -1
               offset[l]+=-1;
               offset[r+1]+=1;
            }
            else{                   // go forward by +1
              offset[l]+=1;
              offset[r+1]+=-1;
            }
        }
        int prefix=0;
        for(int i=0;i<n;i++){
            prefix+=offset[i];
            while(prefix<0) prefix+=26;    // to cycle in 'a'(97) to 'z'(122) (negative remainder thrm)
            int ch=(s[i]-'a'+prefix)%26;  // jb tk 26 ke range me na a jaye tb tk prefix+=26 krte rho
            s[i]=char('a'+ch);
        }
        return s;
    }
};
