	 class Solution {

     public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {

        int n=s.size();
        int blk=sqrt(n);
        int siz=ceil(n*1.0/blk);
        vector<int> f(siz,0);
        vector<int> arr(n,0);

        for(auto x: shifts){

            int l=x[0];
            int r=x[1];
            int add=x[2]==1?1:-1;
