class Solution {
public:
    /* LOGIC:
    + Instead of storing the count of number of times a character is changing 
    using hash table, 
    + Make an array for indexes of string s then mark those indexes where 
    the count of number of times a char changing according to given
    shifts's starting and ending index.
    + And use a count variable to find that a number of times a particular
    char changed in the string s by changing the count variable using array
    on the go.

    e.g
    For, s = "abc", shifts = [[0,1,0],[1,2,1],[0,2,1]]
    // initially, markArray = [0, 0, 0, 0] (means every char in s changed 0 times, taking the markArray size = n+1 for simplicity purpose)
    for idx = 0 in shifts:
    markArray = [-1, 0, +1, 0] 
    for idx = 1 in shifts:
    markArray = [-1, 0+(1), +1, 0+(-1)]
    for idx = 2 in shifts:
    markArray = [-1+(+1), +1, +1, -1+(-1)]

    final markArray = [0, 1, 1, 2]
    which means,
    cnt = 0 // initially
    for i = 0:
    cnt += markArray[0] => cnt = 0
    s[0] not changed, ie 'a' => 'a'
    for i = 1:
    cnt += markArray[1] => cnt = 1
    s[1] incremented by cnt=1, ie 'b' => 'c'
    for i = 2:
    cnt += markArray[1] => cnt = 2
    s[2] incremented by cnt=2, ie 'c' => 'e'

    output=>'ace'
    */
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n = s.length();
        vector<long long> shiftcnt (n+1, 0);
        for (int i=0; i<shifts.size(); i++) {
            if (shifts[i][2]==1) {
                shiftcnt[shifts[i][0]] += 1;
                shiftcnt[shifts[i][1]+1] -= 1; 
            }
            else {
                shiftcnt[shifts[i][0]] -= 1;
                shiftcnt[shifts[i][1]+1] += 1; 
            }
        }

        string chrs = "abcdefghijklmnopqrstuvwxyz";

        long long cnt = 0;
        for (int i=0; i<n; i++) {
            cnt += shiftcnt[i];
            int nshift = cnt%26;
            int c_idx = s[i]-'a';
            if (nshift>=0) {
                if (c_idx+nshift<=25) {
                    s[i] = chrs[c_idx+nshift];
                } else {
                    s[i] = chrs[c_idx+nshift-26];
                }
            }
            else {
                if (c_idx+nshift>=0) {
                    s[i] = chrs[c_idx+nshift];
                } else {
                    s[i] = chrs[26+c_idx+nshift];
                }
            }
        }

        return s;
    }
};
