    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n = s.length();
        vector<long long> shiftcnt (n+1, 0);
        for (int i=0; i<shifts.size(); i++) {
            if (shifts[i][2]==1) {
                shiftcnt[shifts[i][0]] += 1;
                shiftcnt[shifts[i][1]+1] -= 1; 
            }
            else {
                shiftcnt[shifts[i][0]] -= 1;
                shiftcnt[shifts[i][1]+1] += 1; 
            }
        }

        string chrs = "abcdefghijklmnopqrstuvwxyz";

        long long cnt = 0;
        for (int i=0; i<n; i++) {
            cnt += shiftcnt[i];
            int nshift = cnt%26;
            int c_idx = s[i]-'a';
            if (nshift>=0) {
                if (c_idx+nshift<=25) {
                    s[i] = chrs[c_idx+nshift];
                } else {
                    s[i] = chrs[c_idx+nshift-26];
                }
            }
            else {
                if (c_idx+nshift>=0) {
                    s[i] = chrs[c_idx+nshift];
                } else {
                    s[i] = chrs[26+c_idx+nshift];
                }
            }
        }

        return s;
    }
};
