class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.size();
        vector<int> pref(s.size()+1,0);
        for(int i=0;i<shifts.size();i++)
        {
            int z=shifts[i][2],l=shifts[i][0],r=shifts[i][1];
            if(z==0) // suppose given string of size 4 so initially pref array 0,0,0,0,0, and given range from (1,3)
            {
                pref[l]-=1; // for -1 case 0 -1 0 0 1 
                pref[r+1]+=1;
            }
            else
            {
                pref[l]+=1; // for the +1 case 0 1 0 0 -1  
                pref[r+1]-=1;
            }
        }
        for(int i=1;i<pref.size();i++)
        {
              pref[i]+=pref[i-1]; // calculating pref sum and we see that for +1 case 0 1 1 1 0  and for -1 case 0 -1 -1 -1 0 
        }
        for(int i=0;i<s.size();i++)
        {
            if(pref[i]<0) // if pref[i]<0 
            {
                int z=abs(pref[i]); // taking absolute value and converting it into multiple of z if modulo 0 then taking direct value by division or +1 in case not divisible
                if(z%26==0) 
                z=z/26;
                else
                z=z/26+1;
                s[i]=(s[i]-'a'+pref[i]+z*26)%26+'a';
            }
            else
                s[i]=(s[i]-'a'+pref[i])%26+'a';
        }
        return s;
    }
};
