class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n = s.size();
        vector<int> f(n);

        for(vector<int> a : shifts){
            if(a[2] == 0){
                f[a[0]] -= 1;
                if(a[1] + 1 < n) f[a[1] + 1] += 1;
            }
            else{
                f[a[0]] += 1;
                if(a[1] + 1 < n) f[a[1] + 1] -= 1;
            }
