class Solution {
public:
    // Solution based on Segment Tree and Lazy propagation, I feel this solution is not optimal and can be optimized a lot.

    #define M (beg+end)/2
    void pushdown(int pos, int beg, int end,vector<int> &lazy, vector<int> &st)
    {
        if (lazy[pos] > 0)
        {
            st[pos] += (end - beg + 1) * lazy[pos];
            st[pos]%=26;
            if (beg != end)
            {
                lazy[2 * pos + 1] =(lazy[2 * pos + 1] + lazy[pos])%26;
                lazy[2 * pos + 2] =(lazy[2 * pos + 2] + lazy[pos])%26;
            }
            lazy[pos] = 0;
        }
    }

    void update(int beg, int end, int ul, int ur, int val, int pos,vector<int> &lazy,vector<int> &st)
    {
        pushdown(pos, beg, end,lazy,st);
        if (ul <= beg && ur >= end)
        {
            lazy[pos] += val;
            lazy[pos]%=26;
            pushdown(pos, beg, end,lazy,st);
            return;
        }
        if (ul > end || ur < beg)
            return;
        update(beg, M, ul, ur, val, 2 * pos + 1,lazy,st);
        update(M + 1, end, ul, ur, val, 2 * pos + 2,lazy,st);
        st[pos] = st[2 * pos + 1] + st[2 * pos + 2];
        st[pos]%=26;
    }

    int query(int beg, int end, int ql, int qr, int pos, vector<int> &lazy, vector<int> &st)
    {
        pushdown(pos, beg, end,lazy,st);
        if (ql <= beg && qr >= end)
        {
            return st[pos];
        }
        if (ql > end || qr < beg)
            return 0;
        return (query(beg, M, ql, qr, 2 * pos + 1,lazy,st) + query(M + 1, end, ql, qr, 2 * pos + 2,lazy,st))%26;
    }

    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.length();
        int m=shifts.size();
        vector<int> st(4*n);
        vector<int> lazy(4*n);
        for(int i=0; i<m; i++) {
            if(shifts[i][2]==0){
                update(0,n-1,shifts[i][0],shifts[i][1],25,0,lazy,st);
            }else{
                update(0,n-1,shifts[i][0],shifts[i][1],1,0,lazy,st);
            }
        }
        string ans="";
        for(int i=0; i<n; i++){
            // cout<<"query : "<<query(0,n-1,i,i,0,lazy,st)<<endl;
            ans+=(char('a'+((((s[i]-'a'+query(0,n-1,i,i,0,lazy,st))%26)+26)%26)));
        }
        return ans;

    }
};
