class Solution {
    public String shiftingLetters(String s, int[][] shifts) {
        int[] ch=new int[s.length()];
        for(int[] shift:shifts){
            int start=shift[0];
            int end=shift[1];
            int add=shift[2]==0?-1:1;
            ch[start]+=add;
            if(end+1<ch.length)
                ch[end+1]-=add;
        }
        int sum=0;
        for(int i=0;i<ch.length;i++){
            sum+=ch[i];
            ch[i]=sum;
        }

        StringBuilder sb=new StringBuilder();
        for(int i=0;i<s.length();i++){
            char c=s.charAt(i);
            int shift=ch[i];
            if(shift>=0)
                shift=ch[i]%26;
            else
                shift=ch[i]%26+26;

            if(shift==0)
                sb.append(s.charAt(i));
            else {
                int diff = c + shift;
                if (diff < 123)
                    sb.append((char) diff);
                else
                    sb.append((char) (diff - 123 + 'a'));
            }

        }
        return sb.toString();
    }
}
