        int n = s.length();
        int [] dir = new int[n + 1];

        for(int [] shift : shifts){
            int i = shift[0];
            int j = shift[1];
            int move = (shift[2] == 0 ? -1 : 1);
            dir[i] += move;
            dir[j + 1] -= move;
        }

        //now take the prefix sums
        for(int i = 1; i < n + 1; i++){
            dir[i] += dir[i - 1];
        }

        char [] res = new char[n];

        for(int i = 0; i < n; i++){
            int idx = (s.charAt(i) - 'a' + dir[i]) % 26;
            if(idx < 0){
                idx += 26;
            }

           char newCh = (char)(idx + 'a'); 
            res[i] = newCh;
        }

        return new String(res);
    }
}

