class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        // O(s.length() * shifts.length())  Brute Force....
        // We can optimmize thiis s.length() part, try and remove this
        // Preprocess the shifts...
        // arr of length of string + bookeeping in array = [{+|- freq},......]
        // +, - signifying forward or backward shifts
        // Go through the array uppdating the string ans. 
        vector<int> bookeeping(s.length() + 1, 0);
        for(int i=0;i<shifts.size();i++){
          bool isfrwrd = shifts[i][2] == 1 ? true : false;
          if(isfrwrd){
            bookeeping[shifts[i][0]] += 1;
            bookeeping[shifts[i][1] + 1] -= 1; 
          }else{
            bookeeping[shifts[i][0]] -= 1;
            bookeeping[shifts[i][1] + 1] += 1; 
          }
        }
        int running = 0;
        for(int i=0;i<s.length();i++){
          int k = bookeeping[i]%26;
          running += k;
          running %= 26;
          int newVar = running + (s[i] - 'a'); // New position of the updated character
          newVar = (newVar + 26)%26; // Handle Negatives..... 
          s[i] = newVar + 'a'; 
        }
        return s;
    }
};
