class Solution:
    def shiftingLetters(self, s: str, shifts: List[List[int]]) -> str:
        base = ord("a")
        s_int = [ord(c)-base for c in s]
        cnt = [0] * len(s)
        for start,end,direction in shifts:
            direction = 2*direction - 1
            cnt[start] += direction
            if end < len(s)-1:
                cnt[end+1] -= direction
        delta = cnt[0]
        s_int[0] = (s_int[0] + delta) % 26
        for i in range(1,len(cnt)):
            delta = (delta + cnt[i]) % 26
            s_int[i] = (s_int[i] + delta) % 26
        return "".join([chr(i+base) for i in s_int])
