class Solution {
public:
    string shiftingLetters(string &s, vector<vector<int>>& shifts) {
        int n=s.size(),sum=0,turn;
        vector<int> dif(n+1);
        for(auto &u:shifts){
            if(u[2]&1) dif[u[0]]++,dif[u[1]+1]--; 
            else   dif[u[0]]-- ,  dif[u[1]+1]++;
        }
        for(int i=0;i<n;i++){
            sum = (sum+dif[i])%26;
            turn =s[i]-'a'+sum+26;
            s[i] = 'a' + turn %26;
        }
        return s;
    }
};
